function onLoad() {
   //Type appropriate comment here, and begin script below

   if (g_form.isNewRecord()){
	   return;
   }

   var numberStr = g_form.getValue('number');
   var ch, len, index, count;

   var counts = {};
   len = numberStr.length;

   for (index=0; index<len; ++index) {
	   ch = numberStr.charAt(index);
		alert("ch["+index+"]:"+ ch);

	   count = counts[ch];
	   alert("ch["+index+"]ã®count:" + count);

	   counts[ch] = count ? count + 1 : 1;
   }

	for (ch in counts) {
		alert(ch + " count: " + counts[ch]);
		jslog(ch + " count: " + counts[ch]);
	}
}
